CREATE OR REPLACE PROCEDURE sp_updateproductcategories(oldcategoryid integer, newcategoryid integer, effectivedate timestamp without time zone DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    productcount integer;
    updatedcount integer := 0;
    err_msg text;
    rec record;
BEGIN
    IF effectivedate IS NULL THEN
        effectivedate := current_timestamp;
    END IF;

    IF oldcategoryid = newcategoryid THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO productcount FROM products WHERE categoryid = oldcategoryid AND status = 'ACTIVE';

    IF productcount = 0 THEN
        RAISE NOTICE 'No active products found in category %', oldcategoryid;
        RETURN;
    END IF;

    FOR rec IN
        SELECT productid, productname
        FROM products
        WHERE categoryid = oldcategoryid AND status = 'ACTIVE'
    LOOP
        BEGIN
            INSERT INTO productcategoryhistory (productid, oldcategoryid, newcategoryid, changedate, changedby)
            VALUES (rec.productid, oldcategoryid, newcategoryid, effectivedate, current_user);

            UPDATE products
            SET categoryid = newcategoryid,
                lastmodifieddate = effectivedate,
                lastmodifiedby = current_user
            WHERE productid = rec.productid;

            updatedcount := updatedcount + 1;
        EXCEPTION WHEN OTHERS THEN
            GET DIAGNOSTICS err_msg = MESSAGE_TEXT;
            INSERT INTO categoryupdateerrors (productid, errormessage, errordate)
            VALUES (rec.productid, err_msg, current_timestamp);
        END;
    END LOOP;

    UPDATE productcategories
    SET lastupdatedate = effectivedate
    WHERE categoryid = oldcategoryid OR categoryid = newcategoryid;

    RAISE NOTICE 'Category update completed. % products updated out of % total.', updatedcount, productcount;

EXCEPTION WHEN OTHERS THEN
    GET DIAGNOSTICS err_msg = MESSAGE_TEXT;
    RAISE EXCEPTION 'Category update failed: %', err_msg;
END;
$$;

CREATE OR REPLACE PROCEDURE sp_updateproductcategories(oldcategoryid int, newcategoryid int, effectivedate timestamp DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
  productcount int;
  updatedcount int := 0;
  rec RECORD;
BEGIN
  IF effectivedate IS NULL THEN
    effectivedate := now();
  END IF;

  IF oldcategoryid = newcategoryid THEN
    RAISE EXCEPTION 'Old and new category IDs cannot be the same';
  END IF;

  SELECT COUNT(*) INTO productcount
  FROM products
  WHERE categoryid = oldcategoryid AND status = 'ACTIVE';

  IF productcount = 0 THEN
    RAISE NOTICE 'No active products found in category %', oldcategoryid;
    RETURN;
  END IF;

  FOR rec IN
    SELECT productid, productname
    FROM products
    WHERE categoryid = oldcategoryid AND status = 'ACTIVE'
  LOOP
    BEGIN
      INSERT INTO productcategoryhistory (productid, oldcategoryid, newcategoryid, changedate, changedby)
      VALUES (rec.productid, oldcategoryid, newcategoryid, effectivedate, current_user);

      UPDATE products
      SET categoryid = newcategoryid,
          lastmodifieddate = effectivedate,
          lastmodifiedby = current_user
      WHERE productid = rec.productid;

      updatedcount := updatedcount + 1;
    EXCEPTION WHEN OTHERS THEN
      INSERT INTO categoryupdateerrors (productid, errormessage, errordate)
      VALUES (rec.productid, SQLERRM, now());
    END;
  END LOOP;

  UPDATE productcategories
  SET lastupdatedate = effectivedate
  WHERE categoryid IN (oldcategoryid, newcategoryid);

  RAISE NOTICE 'Category update completed. % products updated out of % total.', updatedcount, productcount;

EXCEPTION WHEN OTHERS THEN
  RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(IN OldCategoryID INT, IN NewCategoryID INT, IN EffectiveDate TIMESTAMP WITHOUT TIME ZONE DEFAULT NULL)
AS
DECLARE
  ProductCount INT;
  UpdatedCount INT := 0;
  v_message TEXT;
  rec RECORD;
BEGIN
  IF EffectiveDate IS NULL THEN
    EffectiveDate := NOW();
  END IF;

  IF OldCategoryID = NewCategoryID THEN
    RAISE EXCEPTION 'Old and new category IDs cannot be the same';
  END IF;

  SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';

  IF ProductCount = 0 THEN
    RAISE NOTICE 'No active products found in category %', OldCategoryID;
    RETURN;
  END IF;

  FOR rec IN SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE'
  LOOP
    BEGIN
      INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
      VALUES (rec.ProductID, OldCategoryID, NewCategoryID, EffectiveDate, current_user);

      UPDATE Products
      SET CategoryID = NewCategoryID,
          LastModifiedDate = EffectiveDate,
          LastModifiedBy = current_user
      WHERE ProductID = rec.ProductID;

      UpdatedCount := UpdatedCount + 1;
    EXCEPTION WHEN OTHERS THEN
      GET STACKED DIAGNOSTICS v_message = MESSAGE_TEXT;
      INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
      VALUES (rec.ProductID, v_message, NOW());
    END;
  END LOOP;

  UPDATE ProductCategories
  SET LastUpdateDate = EffectiveDate
  WHERE CategoryID IN (OldCategoryID, NewCategoryID);

  RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;

EXCEPTION WHEN OTHERS THEN
  GET STACKED DIAGNOSTICS v_message = MESSAGE_TEXT;
  RAISE EXCEPTION 'Category update failed: %', v_message;
END;

CREATE OR REPLACE PROCEDURE GETEMPLOYEESTATS(p_dept_id IN NUMBER, p_result OUT CLOB) IS
  TYPE stats_rec IS RECORD (
    total_emp NUMBER,
    avg_salary NUMBER,
    max_salary NUMBER,
    active_projects NUMBER
  );
  TYPE p_result_rec IS RECORD (
    employee_id NUMBER,
    last_name VARCHAR2(50),
    salary NUMBER(8,2)
  );
  v_stats stats_rec;
  v_result p_result_rec;
  v_cursor SYS_REFCURSOR;
  v_sql VARCHAR2(1000);
  v_dept_name VARCHAR2(50);
  v_first BOOLEAN := TRUE;
BEGIN
  SELECT department_name INTO v_dept_name FROM DEPARTMENTS WHERE department_id = p_dept_id;

  SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_stats.total_emp, v_stats.avg_salary, v_stats.max_salary
    FROM EMPLOYEES WHERE department_id = p_dept_id;

  SELECT COUNT(*)
    INTO v_stats.active_projects
    FROM PROJECTS
    WHERE department_id = p_dept_id AND status = 'ACTIVE';

  v_sql := 'SELECT * FROM (SELECT employee_id, last_name, salary FROM employees WHERE department_id = :1 ORDER BY salary DESC) WHERE ROWNUM <= 5';
  OPEN v_cursor FOR v_sql USING p_dept_id;

  p_result := TO_CLOB('{  "department": "') || TO_CLOB(v_dept_name) ||
              TO_CLOB('",  "total_employees": ') || TO_CLOB(TO_CHAR(v_stats.total_emp)) ||
              TO_CLOB(',  "salary_stats": {    "average": ') || TO_CLOB(TO_CHAR(v_stats.avg_salary)) ||
              TO_CLOB(',    "max": ') || TO_CLOB(TO_CHAR(v_stats.max_salary)) ||
              TO_CLOB('  },  "active_projects": ') || TO_CLOB(TO_CHAR(v_stats.active_projects)) ||
              TO_CLOB(',  "top_earners": [');

  LOOP
    FETCH v_cursor INTO v_result;
    EXIT WHEN v_cursor%NOTFOUND;

    IF v_first THEN
      v_first := FALSE;
    ELSE
      p_result := p_result || TO_CLOB(',');
    END IF;

    p_result := p_result ||
                TO_CLOB('{"id": ') || TO_CLOB(TO_CHAR(v_result.employee_id)) ||
                TO_CLOB(', "name": "') || TO_CLOB(v_result.last_name) ||
                TO_CLOB('", "salary": ') || TO_CLOB(TO_CHAR(v_result.salary)) ||
                TO_CLOB('}');
  END LOOP;

  p_result := p_result || TO_CLOB('  ]}');

  CLOSE v_cursor;
EXCEPTION
  WHEN NO_DATA_FOUND THEN
    p_result := TO_CLOB('{"error": "Department not found"}');
  WHEN OTHERS THEN
    p_result := TO_CLOB('{"error": "') || TO_CLOB(SQLERRM) || TO_CLOB('"}');
END;

