CREATE OR REPLACE FUNCTION archive_old_records(p_archive_date DATE)
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
  v_deleted_rows integer;
BEGIN
  WITH moved AS (
    DELETE FROM active_data
    WHERE record_date < p_archive_date
    RETURNING record_id, record_data
  )
  INSERT INTO archive_table (record_id, record_data)
  SELECT record_id, record_data FROM moved;

  GET DIAGNOSTICS v_deleted_rows = ROW_COUNT;

  INSERT INTO audit_log (log_message) VALUES (v_deleted_rows::text || ' rows archived.');
END;
$$;

CREATE OR REPLACE FUNCTION GETEMPLOYEESTATS(p_dept_id numeric) RETURNS text AS $$
DECLARE
  v_total_emp integer;
  v_avg_salary numeric;
  v_max_salary numeric;
  v_active_projects integer;
  v_dept_name text;
  v_result text;
  rec record;
  err_text text;
BEGIN
  SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
  IF v_dept_name IS NULL THEN
    RETURN '{"error": "Department not found"}';
  END IF;

  SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_total_emp, v_avg_salary, v_max_salary
  FROM employees
  WHERE department_id = p_dept_id;

  SELECT COUNT(*) INTO v_active_projects
  FROM projects
  WHERE department_id = p_dept_id AND status = 'ACTIVE';

  v_result := '{  "department": "' || replace(coalesce(v_dept_name, ''), '"', '\"') || '",  "total_employees": ' || coalesce(v_total_emp, 0) || ',  "salary_stats": {    "average": ' || coalesce(v_avg_salary, 0) || ',    "max": ' || coalesce(v_max_salary, 0) || '  },  "active_projects": ' || coalesce(v_active_projects, 0) || ',  "top_earners": [';

  FOR rec IN
    SELECT employee_id, last_name, salary
    FROM employees
    WHERE department_id = p_dept_id
    ORDER BY salary DESC
    LIMIT 5
  LOOP
    v_result := v_result || '    {"id": ' || rec.employee_id || ', "name": "' || replace(coalesce(rec.last_name, ''), '"', '\"') || '", "salary": ' || coalesce(rec.salary, 0) || '},';
  END LOOP;

  v_result := rtrim(v_result, ',') || '  ]}';
  RETURN v_result;

EXCEPTION
  WHEN OTHERS THEN
    GET STACKED DIAGNOSTICS err_text = MESSAGE_TEXT;
    RETURN '{"error": "' || replace(coalesce(err_text, 'Unknown error'), '"', '\"') || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION usp_updateorderstatus(p_customerid INT, p_oldstatus VARCHAR(20), p_newstatus VARCHAR(20), OUT p_rowsupdated INT, OUT p_errormessage VARCHAR(200), OUT p_retcode INT)
AS
$$
DECLARE
    v_processing_fee NUMERIC(10,2);
    v_msg TEXT;
BEGIN
    p_rowsupdated := 0;
    p_errormessage := NULL;
    p_retcode := NULL;

    IF p_oldstatus = p_newstatus THEN
        p_errormessage := '新旧状态不能相同';
        p_retcode := 400;
        RETURN;
    END IF;

    BEGIN
        UPDATE Orders
        SET Status = p_newstatus,
            LastUpdated = NOW()
        WHERE CustomerID = p_customerid
          AND Status = p_oldstatus
          AND OrderDate > (NOW() + INTERVAL '-3 month');

        GET DIAGNOSTICS p_rowsupdated = ROW_COUNT;

        IF p_rowsupdated = 0 THEN
            p_errormessage := '未找到符合条件的订单';
            p_retcode := 404;
            RETURN;
        END IF;

        INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
        SELECT OrderID, p_oldstatus, p_newstatus, NOW()
        FROM Orders
        WHERE CustomerID = p_customerid
          AND Status = p_newstatus;

        FOR v_processing_fee IN
            SELECT Fee
            FROM OrderDetails
            WHERE OrderID IN (
                SELECT OrderID FROM Orders WHERE CustomerID = p_customerid AND Status = p_newstatus
            )
        LOOP
            v_processing_fee := v_processing_fee * 0.95;
        END LOOP;

        p_errormessage := '更新成功';
        p_retcode := 200;
        RETURN;

    EXCEPTION WHEN OTHERS THEN
        GET STACKED DIAGNOSTICS v_msg = MESSAGE_TEXT;
        p_errormessage := v_msg;
        p_retcode := 500;
        RETURN;
    END;
END;
$$
LANGUAGE plpgsql;

