CREATE OR REPLACE FUNCTION trg_audit_emp_sal_fn() RETURNS trigger AS $$
BEGIN
  INSERT INTO emp_audit(emp_id, old_sal, new_sal) VALUES (OLD.empno, OLD.sal, NEW.sal);
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

