CREATE OR REPLACE PROCEDURE "HumanResources"."uspUpdateEmployeePersonalInfo"(IN p_BusinessEntityID integer, IN p_NationalIDNumber varchar(15), IN p_BirthDate timestamp)
LANGUAGE plpgsql
AS $$
BEGIN
  UPDATE "HumanResources"."Employee"
     SET "NationalIDNumber" = p_NationalIDNumber,
