CREATE OR REPLACE FUNCTION checkandupdatesalaries(
  p_dept_id numeric,
  INOUT p_result text,
  p_percent numeric,
  p_min_dept numeric DEFAULT NULL,
  p_max_dept numeric DEFAULT NULL
) RETURNS text
LANGUAGE plpgsql
AS $$
DECLARE
  v_total_emp numeric;
  v_avg_salary numeric;
  v_max_salary numeric;

  v_employee_id numeric;
  v_last_name varchar(50);
  v_salary numeric(8,2);

  v_dept_name varchar(50);
  v_rows_updated integer := 0;
  v_total_budget numeric := 0;
  dept_rec record;
BEGIN
  BEGIN
    SELECT department_name
      INTO v_dept_name
      FROM departments
     WHERE department_id = p_dept_id;
  EXCEPTION WHEN NO_DATA_FOUND THEN
    p_result := '{"error": "Department not found"}';
    RETURN p_result;
  END;

  SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees
   WHERE department_id = p_dept_id;

  p_result :=
    '{ "department": "' || v_dept_name ||
    '", "total_employees": ' || COALESCE(v_total_emp,0) ||
    ', "salary_stats": { "average": ' || COALESCE(v_avg_salary,0) ||
    ', "max": ' || COALESCE(v_max_salary,0) ||
    ' }, "top_earners": [';

  FOR v_employee_id, v_last_name, v_salary IN
    SELECT employee_id, last_name, salary
      FROM employees
     WHERE department_id = p_dept_id
     ORDER BY salary DESC
     LIMIT 5
  LOOP
    p_result := p_result ||
      ' {"id": ' || v_employee_id ||
      ', "name": "' || v_last_name ||
      '", "salary": ' || COALESCE(v_salary,0) ||
      '},';
  END LOOP;

  p_result := rtrim(p_result, ',') || ' ] }';

  FOR dept_rec IN
    SELECT department_id, budget
      FROM departments
     WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
       AND (department_id <= p_max_dept OR p_max_dept IS NULL)
  LOOP
    UPDATE employees
       SET salary = COALESCE(salary, 3000) * (1 + p_percent/100)
     WHERE department_id = dept_rec.department_id;

    GET DIAGNOSTICS v_rows_updated = v_rows_updated + ROW_COUNT;

    SELECT SUM(salary)
      INTO v_total_budget
      FROM employees
     WHERE department_id = dept_rec.department_id;

    IF v_total_budget > dept_rec.budget THEN
      RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id
        USING ERRCODE = 'P0001';
    END IF;
  END LOOP;

  RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();
  RETURN p_result;

EXCEPTION
  WHEN OTHERS THEN
    p_result := '{"error": "' || replace(SQLERRM, '"', '\"') || '"}';
    RETURN p_result;
END;
$$;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id numeric, OUT p_result text, p_percent numeric, p_min_dept numeric DEFAULT NULL, p_max_dept numeric DEFAULT NULL)
RETURNS text
LANGUAGE plpgsql
AS $$
DECLARE
    v_total_emp numeric;
    v_avg_salary numeric;
    v_max_salary numeric;

    v_employee_id numeric;
    v_last_name varchar(50);
    v_salary numeric(8,2);

    v_dept_name varchar(50);
    v_rows_updated numeric := 0;
    v_total_budget numeric := 0;

    dept_rec record;
    emp_rec record;

    dept_cur CURSOR FOR
        SELECT department_id, budget
        FROM departments
        WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
          AND (department_id <= p_max_dept OR p_max_dept IS NULL);
BEGIN
    BEGIN
        SELECT department_name
        INTO v_dept_name
        FROM departments
        WHERE department_id = p_dept_id;
    EXCEPTION WHEN no_data_found THEN
        p_result := '{"error": "Department not found"}';
        RETURN;
    END;

    SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees
    WHERE department_id = p_dept_id;

    p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || COALESCE(v_total_emp::text,'0') ||
                ', "salary_stats": { "average": ' || COALESCE(v_avg_salary::text,'null') ||
                ', "max": ' || COALESCE(v_max_salary::text,'null') ||
                ' }, "top_earners": [';

    FOR emp_rec IN
        SELECT employee_id, last_name, salary
        FROM employees
        WHERE department_id = p_dept_id
        ORDER BY salary DESC
        LIMIT 5
    LOOP
        v_employee_id := emp_rec.employee_id;
        v_last_name := emp_rec.last_name;
        v_salary := emp_rec.salary;

        p_result := p_result || ' {"id": ' || COALESCE(v_employee_id::text,'null') ||
                    ', "name": "' || COALESCE(replace(v_last_name,'"','\"'),'') ||
                    '", "salary": ' || COALESCE(v_salary::text,'null') || '},';
    END LOOP;

    p_result := rtrim(p_result, ',') || ' ] }';

    OPEN dept_cur;
    LOOP
        FETCH dept_cur INTO dept_rec;
        EXIT WHEN NOT FOUND;

        UPDATE employees
        SET salary = COALESCE(salary, 3000) * (1 + p_percent/100)
        WHERE department_id = dept_rec.department_id;

        GET DIAGNOSTICS v_rows_updated = v_rows_updated + ROW_COUNT;

        SELECT SUM(salary)
        INTO v_total_budget
        FROM employees
        WHERE department_id = dept_rec.department_id;

        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;
    CLOSE dept_cur;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();

EXCEPTION
    WHEN others THEN
        p_result := '{"error": "' || replace(SQLERRM,'"','\"') || '"}';
END;
$$;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id numeric, p_percent numeric, p_min_dept numeric DEFAULT NULL, p_max_dept numeric DEFAULT NULL, OUT p_result text)
LANGUAGE plpgsql
AS $$
DECLARE
  v_total_emp numeric;
  v_avg_salary numeric;
  v_max_salary numeric;
  v_employee_id numeric;
  v_last_name varchar(50);
  v_salary numeric(8,2);
  v_dept_name varchar(50);
  v_rows_updated numeric := 0;
  v_total_budget numeric := 0;
BEGIN
  BEGIN
    SELECT department_name
      INTO v_dept_name
      FROM departments
     WHERE department_id = p_dept_id;
  EXCEPTION WHEN NO_DATA_FOUND THEN
    p_result := '{"error": "Department not found"}';
    RETURN;
  END;

  SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees
   WHERE department_id = p_dept_id;

  p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || COALESCE(v_total_emp::text,'0') ||
              ', "salary_stats": { "average": ' || COALESCE(v_avg_salary::text,'null') ||
              ', "max": ' || COALESCE(v_max_salary::text,'null') || ' }, "top_earners": [';

  FOR v_employee_id, v_last_name, v_salary IN
    SELECT employee_id, last_name, salary
      FROM employees
     WHERE department_id = p_dept_id
     ORDER BY salary DESC
     LIMIT 5
  LOOP
    p_result := p_result || ' {"id": ' || v_employee_id || ', "name": "' || v_last_name || '", "salary": ' || COALESCE(v_salary::text,'null') || '},';
  END LOOP;

  IF right(p_result, 1) = ',' THEN
    p_result := left(p_result, length(p_result) - 1);
  END IF;
  p_result := p_result || ' ] }';

  FOR dept_rec IN
    SELECT department_id, budget
      FROM departments
     WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
       AND (department_id <= p_max_dept OR p_max_dept IS NULL)
  LOOP
    UPDATE employees
       SET salary = COALESCE(salary, 3000) * (1 + p_percent/100)
     WHERE department_id = dept_rec.department_id;

    GET DIAGNOSTICS v_rows_updated = v_rows_updated + ROW_COUNT;

    SELECT SUM(salary)
      INTO v_total_budget
      FROM employees
     WHERE department_id = dept_rec.department_id;

    IF v_total_budget > dept_rec.budget THEN
      RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id
        USING ERRCODE = 'P0001';
    END IF;
  END LOOP;

  RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();

EXCEPTION WHEN OTHERS THEN
  p_result := '{"error": "' || replace(SQLERRM, '"', '\"') || '"}';
END;
$$;

CREATE OR REPLACE FUNCTION SP_BULK_UPDATE_INVENTORY(p_warehouse_id numeric, p_adjustment_type varchar, p_adjustment_date date DEFAULT current_date)
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
  v_batch_size constant integer := 1000;
  v_total_processed integer := 0;
  v_error_count integer := 0;
  rec record;
BEGIN
  FOR rec IN
    SELECT
      product_id,
      quantity_on_hand AS current_qty,
      CASE p_adjustment_type
        WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
        WHEN 'DAMAGE' THEN -damaged_quantity
        WHEN 'RETURN' THEN returned_quantity
        ELSE 0
      END AS adjustment_qty
    FROM inventory_staging
    WHERE warehouse_id = p_warehouse_id
      AND status = 'PENDING'
    ORDER BY product_id
  LOOP
    BEGIN
      UPDATE inventory inv
         SET quantity_on_hand = inv.quantity_on_hand + rec.adjustment_qty,
             last_adjustment_date = p_adjustment_date,
             last_adjustment_type = p_adjustment_type
       WHERE inv.product_id = rec.product_id
         AND inv.warehouse_id = p_warehouse_id;

      IF NOT FOUND THEN
        INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
        VALUES (rec.product_id, p_warehouse_id, GREATEST(0, rec.adjustment_qty), p_adjustment_date, p_adjustment_type);
      END IF;

      INSERT INTO inventory_audit_log
        (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
      VALUES
        (rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.current_qty, rec.adjustment_qty, rec.current_qty + rec.adjustment_qty, current_user);

      v_total_processed := v_total_processed + 1;
    EXCEPTION WHEN OTHERS THEN
      v_error_count := v_error_count + 1;
      INSERT INTO error_log (error_date, error_message, product_id)
      VALUES (current_timestamp, 'Inventory update failed: ' || SQLERRM, rec.product_id);
    END;
  END LOOP;

  UPDATE inventory_staging
     SET status = 'PROCESSED',
         processed_date = current_timestamp
   WHERE warehouse_id = p_warehouse_id
     AND status = 'PENDING';

  RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;

EXCEPTION WHEN OTHERS THEN
  RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM USING ERRCODE = 'P2001';
END;
$$;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id numeric, p_adjustment_type varchar, p_adjustment_date date DEFAULT now()::date)
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
  v_batch_size constant integer := 1000;
  v_total_processed integer := 0;
  v_error_count integer := 0;
  rec record;
BEGIN
  BEGIN
    FOR rec IN
      SELECT
        product_id,
        quantity_on_hand AS current_qty,
        CASE p_adjustment_type
          WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
          WHEN 'DAMAGE' THEN -damaged_quantity
          WHEN 'RETURN' THEN returned_quantity
          ELSE 0
        END AS adjustment_qty
      FROM inventory_staging
      WHERE warehouse_id = p_warehouse_id
        AND status = 'PENDING'
      ORDER BY product_id
    LOOP
      BEGIN
        UPDATE inventory inv
        SET quantity_on_hand = inv.quantity_on_hand + rec.adjustment_qty,
            last_adjustment_date = p_adjustment_date,
            last_adjustment_type = p_adjustment_type
        WHERE inv.product_id = rec.product_id
          AND inv.warehouse_id = p_warehouse_id;

        IF NOT FOUND THEN
          INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
          VALUES (rec.product_id, p_warehouse_id, GREATEST(0, rec.adjustment_qty), p_adjustment_date, p_adjustment_type);
        END IF;

        INSERT INTO inventory_audit_log
          (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
        VALUES
          (rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.current_qty, rec.adjustment_qty, rec.current_qty + rec.adjustment_qty, current_user);

        v_total_processed := v_total_processed + 1;
      EXCEPTION WHEN OTHERS THEN
        v_error_count := v_error_count + 1;
        INSERT INTO error_log (error_date, error_message, product_id)
        VALUES (now(), 'Inventory update failed: ' || SQLERRM, rec.product_id);
      END;
    END LOOP;

    UPDATE inventory_staging
    SET status = 'PROCESSED',
        processed_date = now()
    WHERE warehouse_id = p_warehouse_id
      AND status = 'PENDING';

    COMMIT;

    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
  EXCEPTION WHEN OTHERS THEN
    ROLLBACK;
    RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM USING ERRCODE = 'P0001';
  END;
END;
$$;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id numeric, p_adjustment_type varchar, p_adjustment_date date DEFAULT CURRENT_DATE)
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
  v_batch_size CONSTANT integer := 1000;
  v_total_processed numeric := 0;
  v_error_count numeric := 0;
  rec record;
BEGIN
  BEGIN
    FOR rec IN
      SELECT product_id,
             quantity_on_hand AS current_qty,
             CASE p_adjustment_type
               WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
               WHEN 'DAMAGE'  THEN -damaged_quantity
               WHEN 'RETURN'  THEN returned_quantity
               ELSE 0
             END AS adjustment_qty
      FROM inventory_staging
      WHERE warehouse_id = p_warehouse_id
        AND status = 'PENDING'
      ORDER BY product_id
    LOOP
      BEGIN
        UPDATE inventory inv
           SET quantity_on_hand = inv.quantity_on_hand + rec.adjustment_qty,
               last_adjustment_date = p_adjustment_date,
               last_adjustment_type = p_adjustment_type
         WHERE inv.product_id = rec.product_id
           AND inv.warehouse_id = p_warehouse_id;

        IF NOT FOUND THEN
          INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
          VALUES (rec.product_id, p_warehouse_id, GREATEST(0, rec.adjustment_qty), p_adjustment_date, p_adjustment_type);
        END IF;

        INSERT INTO inventory_audit_log
          (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
        VALUES
          (rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.current_qty, rec.adjustment_qty, rec.current_qty + rec.adjustment_qty, CURRENT_USER);

        v_total_processed := v_total_processed + 1;
      EXCEPTION WHEN OTHERS THEN
        v_error_count := v_error_count + 1;
        INSERT INTO error_log (error_date, error_message, product_id)
        VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, rec.product_id);
      END;
    END LOOP;

    UPDATE inventory_staging
       SET status = 'PROCESSED',
           processed_date = CURRENT_TIMESTAMP
     WHERE warehouse_id = p_warehouse_id
       AND status = 'PENDING';

    COMMIT;
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
  EXCEPTION WHEN OTHERS THEN
    ROLLBACK;
    RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM USING ERRCODE = 'P0001';
  END;
END;
$$;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year numeric, p_quarter numeric, p_sales_rep_id numeric DEFAULT NULL)
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
  v_report_id numeric;
  v_total_sales numeric := 0;
  v_total_commission numeric := 0;
  v_start_date date;
  v_end_date date;
  r record;
BEGIN
  v_start_date := to_date(p_year::text || '-' || lpad(((p_quarter - 1) * 3 + 1)::text, 2, '0') || '-01', 'YYYY-MM-DD');
  v_end_date := (date_trunc('month', (v_start_date + interval '2 months')) + interval '1 month - 1 day')::date;

  SELECT nextval('sales_report_seq') INTO v_report_id;

  INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
  SELECT v_report_id, p_year, p_quarter, now(), count(*)
  FROM (
    SELECT sr.sales_rep_id
    FROM sales_representatives sr
    LEFT JOIN sales s
      ON sr.sales_rep_id = s.sales_rep_id
     AND s.sale_date BETWEEN v_start_date AND v_end_date
    WHERE (p_sales_rep_id IS NULL OR sr.sales_rep_id = p_sales_rep_id)
    GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
  ) x;

  FOR r IN
    SELECT
      sr.sales_rep_id,
      sr.sales_rep_name,
      COALESCE(SUM(s.sale_amount), 0) AS total_sales,
      sr.commission_rate,
      COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate AS commission_amount
    FROM sales_representatives sr
    LEFT JOIN sales s
      ON sr.sales_rep_id = s.sales_rep_id
     AND s.sale_date BETWEEN v_start_date AND v_end_date
    WHERE (p_sales_rep_id IS NULL OR sr.sales_rep_id = p_sales_rep_id)
    GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
    ORDER BY total_sales DESC
  LOOP
    INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
    VALUES (v_report_id, r.sales_rep_id, r.sales_rep_name, r.total_sales, r.commission_rate, r.commission_amount);

    v_total_sales := v_total_sales + r.total_sales;
    v_total_commission := v_total_commission + r.commission_amount;
  END LOOP;

  UPDATE sales_report_header
  SET total_sales = v_total_sales,
      total_commission = v_total_commission
  WHERE report_id = v_report_id;

  RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;

EXCEPTION
  WHEN OTHERS THEN
    RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$;

