CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year numeric, p_quarter numeric, p_sales_rep_id numeric DEFAULT NULL)
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
  v_report_id numeric;
  v_total_sales numeric := 0;
  v_total_commission numeric := 0;
  v_start_date date;
  v_end_date date;
  r RECORD;
BEGIN
  v_start_date := to_date(p_year::text || '-' || lpad(((p_quarter - 1) * 3 + 1)::int::text, 2, '0') || '-01', 'YYYY-MM-DD');
  v_end_date := (date_trunc('month', (v_start_date + interval '2 months')) + interval '1 month - 1 day')::date;

  SELECT nextval('sales_report_seq') INTO v_report_id;

  FOR r IN
    SELECT
      sr.sales_rep_id,
      sr.sales_rep_name,
      COALESCE(SUM(s.sale_amount), 0) AS total_sales,
      sr.commission_rate,
      COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate AS commission_amount
    FROM sales_representatives sr
    LEFT JOIN sales s
      ON sr.sales_rep_id = s.sales_rep_id
     AND s.sale_date BETWEEN v_start_date AND v_end_date
    WHERE (p_sales_rep_id IS NULL OR sr.sales_rep_id = p_sales_rep_id)
    GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
    ORDER BY total_sales DESC
  LOOP
    INSERT INTO sales_report_detail
      (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
    VALUES
      (v_report_id, r.sales_rep_id, r.sales_rep_name, r.total_sales, r.commission_rate, r.commission_amount);

    v_total_sales := v_total_sales + r.total_sales;
    v_total_commission := v_total_commission + r.commission_amount;
  END LOOP;

  INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps, total_sales, total_commission)
  SELECT
    v_report_id,
    p_year,
    p_quarter,
    now(),
    COUNT(*),
    v_total_sales,
    v_total_commission
  FROM sales_report_detail
  WHERE report_id = v_report_id;

  RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;

  RETURN;
EXCEPTION
  WHEN OTHERS THEN
