CREATE OR REPLACE PROCEDURE SP_DataMigration(IN SourceTable NVARCHAR2(128), IN TargetTable NVARCHAR2(128), IN BatchSize INT DEFAULT 10000)
AS
DECLARE
  v_sql TEXT;
  SourceCount INT;
  TargetCount INT;
  BatchCount INT := 0;
  MigrationID INT;
  ErrorMessage NVARCHAR2(4000);
  v_rowcount INT;
  v_status TEXT;
BEGIN
  INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
  VALUES (SourceTable, TargetTable, SYSDATE, 'RUNNING')
  RETURNING MigrationID INTO MigrationID;

  v_sql := 'SELECT COUNT(*) FROM ' || SourceTable;
  EXECUTE IMMEDIATE v_sql INTO SourceCount;

  UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = MigrationID;

  v_sql := 'DELETE FROM ' || TargetTable;
  EXECUTE IMMEDIATE v_sql;

  WHILE BatchCount < SourceCount LOOP
    v_sql := 'INSERT INTO ' || TargetTable ||
             ' SELECT * FROM (SELECT * FROM ' || SourceTable || ' s WHERE NOT EXISTS (SELECT 1 FROM ' || TargetTable || ' t WHERE t.ID = s.ID) LIMIT ' || BatchSize::TEXT || ')';
    EXECUTE IMMEDIATE v_sql;

    GET DIAGNOSTICS v_rowcount = ROW_COUNT;
    IF v_rowcount = 0 THEN
      EXIT;
    END IF;

    BatchCount := BatchCount + v_rowcount;
  END LOOP;

  v_sql := 'SELECT COUNT(*) FROM ' || TargetTable;
  EXECUTE IMMEDIATE v_sql INTO TargetCount;

  v_status := CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END;

  UPDATE MigrationLog
  SET EndTime = SYSDATE, TargetCount = TargetCount, Status = v_status
  WHERE MigrationID = MigrationID;

  RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;
EXCEPTION
  WHEN OTHERS THEN
    ErrorMessage := SQLERRM;
    UPDATE MigrationLog
    SET EndTime = SYSDATE, Status = 'FAILED', ErrorMessage = ErrorMessage
    WHERE MigrationID = MigrationID;
    RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
END;

CREATE OR REPLACE PROCEDURE SP_DataMigration(IN SourceTable NVARCHAR2(128), IN TargetTable NVARCHAR2(128), IN BatchSize INT DEFAULT 10000)
AS
DECLARE
  v_sql TEXT;
  v_sourcecount INT := 0;
  v_targetcount INT := 0;
  v_batchcount INT := 0;
  v_migrationid INT;
  v_errormessage TEXT;
  v_rowcount BIGINT;
BEGIN
  INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
  VALUES (SourceTable, TargetTable, SYSDATE, 'RUNNING')
  RETURNING MigrationID INTO v_migrationid;

  v_sql := 'SELECT COUNT(*) FROM ' || SourceTable;
  EXECUTE IMMEDIATE v_sql INTO v_sourcecount;

  UPDATE MigrationLog SET SourceCount = v_sourcecount WHERE MigrationID = v_migrationid;

  v_sql := 'DELETE FROM ' || TargetTable;
  EXECUTE IMMEDIATE v_sql;

  WHILE v_batchcount < v_sourcecount LOOP
    v_sql := 'INSERT INTO ' || TargetTable ||
             ' SELECT * FROM (SELECT * FROM ' || SourceTable ||
             ' s WHERE NOT EXISTS (SELECT 1 FROM ' || TargetTable || ' t WHERE t.ID = s.ID) LIMIT ' || BatchSize || ') x';
    EXECUTE IMMEDIATE v_sql;

    GET DIAGNOSTICS v_rowcount = ROW_COUNT;
    IF v_rowcount = 0 THEN
      EXIT;
    END IF;

    v_batchcount := v_batchcount + v_rowcount;
  END LOOP;

  v_sql := 'SELECT COUNT(*) FROM ' || TargetTable;
  EXECUTE IMMEDIATE v_sql INTO v_targetcount;

  UPDATE MigrationLog
     SET EndTime = SYSDATE,
         TargetCount = v_targetcount,
         Status = CASE WHEN v_sourcecount = v_targetcount THEN 'SUCCESS' ELSE 'WARNING' END
   WHERE MigrationID = v_migrationid;

  RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, v_sourcecount, v_targetcount;
EXCEPTION
  WHEN OTHERS THEN
    v_errormessage := SQLERRM;
    UPDATE MigrationLog
       SET EndTime = SYSDATE,
           Status = 'FAILED',
           ErrorMessage = v_errormessage
     WHERE MigrationID = v_migrationid;
    RAISE EXCEPTION 'Data migration failed: %', v_errormessage;
END;

CREATE OR REPLACE PROCEDURE SP_DataMigration(IN SourceTable NVARCHAR2(128), IN TargetTable NVARCHAR2(128), IN BatchSize INT DEFAULT 10000)
IS
  v_sql VARCHAR2(32767);
  v_sourcecount INT;
  v_targetcount INT;
  v_batchcount INT := 0;
  v_migrationid INT;
  v_errormessage VARCHAR2(4000);
  v_rows INT;
BEGIN
  INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
  VALUES (SourceTable, TargetTable, SYSDATE, 'RUNNING')
  RETURNING MigrationID INTO v_migrationid;

  v_sql := 'SELECT COUNT(*) FROM ' || SourceTable;
  EXECUTE IMMEDIATE v_sql INTO v_sourcecount;

  UPDATE MigrationLog SET SourceCount = v_sourcecount WHERE MigrationID = v_migrationid;

  v_sql := 'DELETE FROM ' || TargetTable;
  EXECUTE IMMEDIATE v_sql;

  WHILE v_batchcount < v_sourcecount LOOP
    v_sql := 'INSERT INTO ' || TargetTable ||
             ' SELECT * FROM (SELECT * FROM ' || SourceTable ||
             ' s WHERE NOT EXISTS (SELECT 1 FROM ' || TargetTable ||
             ' t WHERE t.ID = s.ID) LIMIT ' || BatchSize || ')';
    EXECUTE IMMEDIATE v_sql;
    GET DIAGNOSTICS v_rows = ROW_COUNT;

    IF v_rows = 0 THEN
      EXIT;
    END IF;

    v_batchcount := v_batchcount + v_rows;
  END LOOP;

  v_sql := 'SELECT COUNT(*) FROM ' || TargetTable;
  EXECUTE IMMEDIATE v_sql INTO v_targetcount;

  UPDATE MigrationLog
     SET EndTime = SYSDATE,
         TargetCount = v_targetcount,
         Status = CASE WHEN v_sourcecount = v_targetcount THEN 'SUCCESS' ELSE 'WARNING' END
   WHERE MigrationID = v_migrationid;

  RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, v_sourcecount, v_targetcount;
EXCEPTION
  WHEN OTHERS THEN
    v_errormessage := SQLERRM;
    UPDATE MigrationLog
       SET EndTime = SYSDATE,
           Status = 'FAILED',
           ErrorMessage = v_errormessage
     WHERE MigrationID = v_migrationid;
    RAISE EXCEPTION 'Data migration failed: %', v_errormessage;
END;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(IN OldCategoryID INT, IN NewCategoryID INT, INOUT EffectiveDate TIMESTAMP DEFAULT NULL)
AS
DECLARE
    ProductCount INT;
    UpdatedCount INT := 0;
    ProductID INT;
    ProductName NVARCHAR2(100);
    v_errmsg TEXT;
    v_cursor CURSOR FOR
        SELECT ProductID, ProductName
        FROM Products
        WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
BEGIN
    IF EffectiveDate IS NULL THEN
        EffectiveDate := CURRENT_TIMESTAMP;
    END IF;

    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO ProductCount
    FROM Products
    WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';

    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;

    OPEN v_cursor;
    LOOP
        FETCH v_cursor INTO ProductID, ProductName;
        EXIT WHEN NOT FOUND;

        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (ProductID, OldCategoryID, NewCategoryID, EffectiveDate, SESSION_USER);

            UPDATE Products
            SET CategoryID = NewCategoryID,
                LastModifiedDate = EffectiveDate,
                LastModifiedBy = SESSION_USER
            WHERE ProductID = ProductID;

            UpdatedCount := UpdatedCount + 1;
        EXCEPTION
            WHEN OTHERS THEN
                GET STACKED DIAGNOSTICS v_errmsg = MESSAGE_TEXT;
                INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                VALUES (ProductID, v_errmsg, CURRENT_TIMESTAMP);
        END;
    END LOOP;

    CLOSE v_cursor;

    UPDATE ProductCategories
    SET LastUpdateDate = EffectiveDate
    WHERE CategoryID IN (OldCategoryID, NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;

EXCEPTION
    WHEN OTHERS THEN
        BEGIN
            CLOSE v_cursor;
        EXCEPTION
            WHEN OTHERS THEN
                NULL;
        END;
        GET STACKED DIAGNOSTICS v_errmsg = MESSAGE_TEXT;
        RAISE EXCEPTION 'Category update failed: %', v_errmsg;
END;

