CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(IN OldCategoryID INT, IN NewCategoryID INT, INOUT EffectiveDate TIMESTAMP DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    ProductCount INT;
    UpdatedCount INT := 0;
    rec RECORD;
BEGIN
    IF EffectiveDate IS NULL THEN
        EffectiveDate := clock_timestamp();
    END IF;

    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO ProductCount
    FROM Products
    WHERE CategoryID = OldCategoryID
      AND Status = 'ACTIVE';

    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;

    FOR rec IN
        SELECT ProductID, ProductName
        FROM Products
        WHERE CategoryID = OldCategoryID
          AND Status = 'ACTIVE'
    LOOP
        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (rec.ProductID, OldCategoryID, NewCategoryID, EffectiveDate, current_user);

            UPDATE Products
            SET CategoryID = NewCategoryID,
                LastModifiedDate = EffectiveDate,
                LastModifiedBy = current_user
            WHERE ProductID = rec.ProductID;

            UpdatedCount := UpdatedCount + 1;
        EXCEPTION WHEN OTHERS THEN
            INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
            VALUES (rec.ProductID, SQLERRM, clock_timestamp());
        END;
    END LOOP;

    UPDATE ProductCategories
    SET LastUpdateDate = EffectiveDate
    WHERE CategoryID IN (OldCategoryID, NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;

EXCEPTION WHEN OTHERS THEN
    RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE FUNCTION bulk_delete_by_ids(p_id_list text) RETURNS void AS $$
DECLARE
  v_sql text;
BEGIN
  v_sql := 'DELETE FROM logs WHERE log_id IN (' || p_id_list || ')';
  EXECUTE v_sql;
  COMMIT;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION archive_old_records(p_archive_date date) RETURNS void AS $$
DECLARE
  v_deleted_rows integer;
BEGIN
  INSERT INTO archive_table (record_id, record_data)
  SELECT record_id, record_data
  FROM active_data
  WHERE record_date < p_archive_date;

  DELETE FROM active_data
  WHERE record_date < p_archive_date;

  GET DIAGNOSTICS v_deleted_rows = ROW_COUNT;

  INSERT INTO audit_log (log_message)
  VALUES (v_deleted_rows::text || ' rows archived.');

  RETURN;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION getemployeestats(p_dept_id numeric, OUT p_result text)
LANGUAGE plpgsql
AS $$
DECLARE
  v_stats_total_emp numeric;
  v_stats_avg_salary numeric;
  v_stats_max_salary numeric;
  v_stats_active_projects numeric;
  v_dept_name varchar(50);
  r record;
BEGIN
  SELECT department_name INTO v_dept_name
  FROM departments
  WHERE department_id = p_dept_id;

  IF NOT FOUND THEN
    p_result := '{"error": "Department not found"}';
    RETURN;
  END IF;

  SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_stats_total_emp, v_stats_avg_salary, v_stats_max_salary
  FROM employees
  WHERE department_id = p_dept_id;

  SELECT COUNT(*)
    INTO v_stats_active_projects
  FROM projects
  WHERE department_id = p_dept_id
    AND status = 'ACTIVE';

  p_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || COALESCE(v_stats_total_emp,0) || ',  "salary_stats": {    "average": ' || COALESCE(v_stats_avg_salary,0) || ',    "max": ' || COALESCE(v_stats_max_salary,0) || '  },  "active_projects": ' || COALESCE(v_stats_active_projects,0) || ',  "top_earners": [';

  FOR r IN
    SELECT employee_id, last_name, salary
    FROM employees
    WHERE department_id = p_dept_id
    ORDER BY salary DESC
    LIMIT 5
  LOOP
    p_result := p_result || '    {"id": ' || r.employee_id || ', "name": "' || r.last_name || '", "salary": ' || r.salary || '},';
  END LOOP;

  p_result := regexp_replace(p_result, ',$', '') || '  ]}';
  RETURN;

EXCEPTION
  WHEN OTHERS THEN
    p_result := '{"error": "' || replace(SQLERRM,'"','\"') || '"}';
    RETURN;
END;
$$;

CREATE OR REPLACE PROCEDURE usp_updateorderstatus(IN p_customerid INT, IN p_oldstatus VARCHAR(20), IN p_newstatus VARCHAR(20), OUT p_rowsupdated INT, OUT p_errormessage VARCHAR(200))
LANGUAGE plpgsql
AS $$
DECLARE
  v_ordercount INT;
  v_trancount INT := 0;
  v_processingfee DECIMAL(10,2) := 0;
  v_fee DECIMAL(10,2);
  fee_cursor CURSOR FOR
    SELECT fee
    FROM orderdetails
    WHERE orderid IN (
      SELECT orderid FROM orders WHERE customerid = p_customerid AND status = p_newstatus
    );
BEGIN
  IF p_oldstatus = p_newstatus THEN
    p_errormessage := '新旧状态不能相同';
    p_rowsupdated := 0;
    RETURN;
  END IF;

  BEGIN
    UPDATE orders
       SET status = p_newstatus,
           lastupdated = CURRENT_TIMESTAMP
     WHERE customerid = p_customerid
       AND status = p_oldstatus
       AND orderdate > (CURRENT_TIMESTAMP - INTERVAL '3 months');

    GET DIAGNOSTICS p_rowsupdated = ROW_COUNT;

    IF p_rowsupdated = 0 THEN
      p_errormessage := '未找到符合条件的订单';
      RETURN;
    END IF;

    INSERT INTO orderlog (orderid, oldstatus, newstatus, changedate)
    SELECT orderid, p_oldstatus, p_newstatus, CURRENT_TIMESTAMP
      FROM orders
     WHERE customerid = p_customerid
       AND status = p_newstatus;

    OPEN fee_cursor;
    FETCH NEXT FROM fee_cursor INTO v_fee;
    WHILE FOUND LOOP
      v_processingfee := v_fee * 0.95;
      FETCH NEXT FROM fee_cursor INTO v_fee;
    END LOOP;
    CLOSE fee_cursor;

    p_errormessage := '更新成功';
    RETURN;
  EXCEPTION
    WHEN OTHERS THEN
      p_errormessage := SQLERRM;
      p_rowsupdated := COALESCE(p_rowsupdated, 0);
      RETURN;
  END;
END;
$$;

