{"case_id": "46", "db_type": "mysql", "description": "此改写技术通过将外层过滤条件下推到子查询内部，减少子查询需要处理的数据规模，尤其对于需要聚合（group by）和窗口计算（row_number）的操作，避免对无效数据进行计算，从而提升整体性能。", "create_table_statements": ["CREATE TABLE users (user_id INT PRIMARY KEY AUTO_INCREMENT, username VARCHAR(100) NOT NULL, status VARCHAR(50) NOT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;", "CREATE INDEX idx_users_status ON users(status);", "CREATE TABLE orders (order_id INT PRIMARY KEY AUTO_INCREMENT, user_id INT NOT NULL, order_date DATE NOT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;", "CREATE INDEX idx_orders_user_id ON orders(user_id);", "CREATE INDEX idx_orders_order_date ON orders(order_date);", "CREATE INDEX idx_orders_user_id_order_date ON orders(user_id, order_date);", "CREATE TABLE order_items (order_item_id INT PRIMARY KEY AUTO_INCREMENT, order_id INT NOT NULL, product_id INT NOT NULL, quantity DECIMAL(10,2) NOT NULL, unit_price DECIMAL(10,2) NOT NULL, discount DECIMAL(5,2) NOT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;", "CREATE INDEX idx_order_items_order_id ON order_items(order_id);", "CREATE INDEX idx_order_items_product_id ON order_items(product_id);", "CREATE TABLE products (product_id INT PRIMARY KEY AUTO_INCREMENT, product_name VARCHAR(255) NOT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;"], "sql": "select /*QB_1*/ u.user_id,
  u.username, COUNT(distinct o.order_id) as total_orders,
  SUM(oi.quantity * oi.unit_price * (1 - oi.discount / 100)) as total_spent,
  AVG(oi.quantity * oi.unit_price * (1 - oi.discount / 100)) as avg_order_value,
  MAX(o.order_date) as last_order_date,
  SQ_1758176107425.GROUP_CONCAT_ as top_products
from users as u
inner join orders as o
on u.user_id = o.user_id 
left outer join order_items as oi
on o.order_id = oi.order_id 
left outer join (
  select /*SQ_1758176107425*/ oi2.user_id,
  GROUP_CONCAT(distinct oi2.product_name order by oi2.product_name separator ', ') as GROUP_CONCAT_
  from (
  select /*QB_2*/ o3.user_id,
  p.product_name,
  row_number() over (partition by o3.user_id order by sum(oi2.quantity * oi2.unit_price * (1 - oi2.discount / 100)) desc) as rank_num
  from order_items as oi2
  inner join products as p
  on oi2.product_id = p.product_id
  inner join orders as o3
  on o3.order_id = oi2.order_id
  group by o3.user_id,
  p.product_name
  ) as oi2
  where oi2.rank_num <= 3 
  group by oi2.user_id 
  ) as SQ_1758176107425
on SQ_1758176107425.user_id = u.user_id
where u.status = 'active'
  and o.order_date >= DATE_SUB(CURRENT_DATE(),
  interval '3' YEAR)
group by u.user_id, u.username
having COUNT(distinct o.order_id) > 0
