ac.tenant_id = 1065587309019136 WHERE (A.tenant_id = 1065587309019136 AND A.acctg_period NOT LIKE '%00' AND (A.base_posted_dr != ifnull(acctg.base_posted_dr1, 0) OR A.base_posted_cr != ifnull(acctg.base_posted_cr1, 0))) AND A.tenant_id = 1065587309019136 LIMIT 50001;

