        UPDATE employees
        SET salary = COALESCE(salary, 3000) * (1 + p_percent/100)
        WHERE department_id = dept_rec.department_id;

        GET DIAGNOSTICS v_row_count = ROW_COUNT;
        v_rows_updated := v_rows_updated + v_row_count;

        SELECT SUM(salary) INTO v_total_budget
        FROM employees
        WHERE department_id = dept_rec.department_id;

        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, CURRENT_TIMESTAMP;

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
    WHEN OTHERS THEN
        p_result := '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(
    p_dept_id INTEGER,
    OUT p_result TEXT,
    p_percent NUMERIC,
    p_min_dept INTEGER DEFAULT NULL,
    p_max_dept INTEGER DEFAULT NULL
)
LANGUAGE plpgsql
AS $BODY$
DECLARE
    v_total_emp INTEGER;
    v_avg_salary NUMERIC;
    v_max_salary NUMERIC;
    v_employee_id INTEGER;
    v_last_name VARCHAR(50);
    v_salary NUMERIC(8,2);
    v_cursor REFCURSOR;
    v_sql TEXT;
    v_dept_name VARCHAR(50);
    v_rows_updated INTEGER := 0;
    v_total_budget NUMERIC := 0;
    v_row_count INTEGER;
    dept_rec RECORD;
BEGIN
    SELECT department_name INTO STRICT v_dept_name
    FROM departments
    WHERE department_id = p_dept_id;

    SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees
    WHERE department_id = p_dept_id;

    v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';

    OPEN v_cursor FOR EXECUTE v_sql USING p_dept_id;

    p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_total_emp::TEXT || ', "salary_stats": { "average": ' || COALESCE(v_avg_salary::TEXT, 'null') || ', "max": ' || COALESCE(v_max_salary::TEXT, 'null') || ' }, "top_earners": [';

    LOOP
        FETCH v_cursor INTO v_employee_id, v_last_name, v_salary;
        EXIT WHEN NOT FOUND;
        p_result := p_result || ' {"id": ' || v_employee_id::TEXT || ', "name": "' || v_last_name || '", "salary": ' || v_salary::TEXT || '},';
    END LOOP;

    p_result := RTRIM(p_result, ',') || ' ] }';

    CLOSE v_cursor;

    FOR dept_rec IN 
        SELECT department_id, budget 
        FROM departments
        WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
        AND (department_id <= p_max_dept OR p_max_dept IS NULL)
    LOOP
        UPDATE employees
        SET salary = COALESCE(salary, 3000) * (1 + p_percent/100)
        WHERE department_id = dept_rec.department_id;

        GET DIAGNOSTICS v_row_count = ROW_COUNT;
        v_rows_updated := v_rows_updated + v_row_count;

        SELECT SUM(salary) INTO v_total_budget
        FROM employees
        WHERE department_id = dept_rec.department_id;

        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, NOW();

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
    WHEN OTHERS THEN
        p_result := '{"error": "' || SQLERRM || '"}';
END;
$BODY$;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id INTEGER, p_adjustment_type VARCHAR, p_adjustment_date DATE DEFAULT CURRENT_DATE) RETURNS VOID AS $$
DECLARE
    v_batch_size CONSTANT INTEGER := 1000;
    v_total_processed INTEGER := 0;
    v_error_count INTEGER := 0;
    v_rec RECORD;
    v_exists BOOLEAN;
BEGIN
    FOR v_rec IN 
        SELECT 
            product_id,
            quantity_on_hand AS current_qty,
            CASE p_adjustment_type
                WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                WHEN 'DAMAGE' THEN -damaged_quantity
                WHEN 'RETURN' THEN returned_quantity
                ELSE 0
            END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id
        AND status = 'PENDING'
        ORDER BY product_id
    LOOP
        BEGIN
            SELECT EXISTS(
                SELECT 1 FROM inventory 
                WHERE product_id = v_rec.product_id 
                AND warehouse_id = p_warehouse_id
            ) INTO v_exists;

            IF v_exists THEN
                UPDATE inventory
                SET quantity_on_hand = quantity_on_hand + v_rec.adjustment_qty,
                    last_adjustment_date = p_adjustment_date,
                    last_adjustment_type = p_adjustment_type
                WHERE product_id = v_rec.product_id
                AND warehouse_id = p_warehouse_id;
            ELSE
                INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                VALUES (v_rec.product_id, p_warehouse_id, GREATEST(0, v_rec.adjustment_qty), p_adjustment_date, p_adjustment_type);
            END IF;

            INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
            VALUES (v_rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, v_rec.current_qty, v_rec.adjustment_qty, v_rec.current_qty + v_rec.adjustment_qty, current_user);

            v_total_processed := v_total_processed + 1;
        EXCEPTION
            WHEN OTHERS THEN
                v_error_count := v_error_count + 1;
                INSERT INTO error_log (error_date, error_message, product_id)
                VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, v_rec.product_id);
        END;
    END LOOP;

    UPDATE inventory_staging
    SET status = 'PROCESSED',
        processed_date = CURRENT_TIMESTAMP
    WHERE warehouse_id = p_warehouse_id
    AND status = 'PENDING';

    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(
    p_warehouse_id INTEGER,
    p_adjustment_type VARCHAR,
    p_adjustment_date DATE DEFAULT CURRENT_DATE
) RETURNS VOID AS $$
DECLARE
    v_inventory RECORD;
    v_total_processed INTEGER := 0;
    v_error_count INTEGER := 0;
    v_updated INTEGER;
BEGIN
    FOR v_inventory IN 
        SELECT 
            product_id,
            quantity_on_hand AS current_qty,
            CASE p_adjustment_type
                WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                WHEN 'DAMAGE' THEN -damaged_quantity
                WHEN 'RETURN' THEN returned_quantity
                ELSE 0
            END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id
            AND status = 'PENDING'
        ORDER BY product_id
    LOOP
        BEGIN
            UPDATE inventory
            SET quantity_on_hand = quantity_on_hand + v_inventory.adjustment_qty,
                last_adjustment_date = p_adjustment_date,
                last_adjustment_type = p_adjustment_type
            WHERE product_id = v_inventory.product_id
                AND warehouse_id = p_warehouse_id;

            GET DIAGNOSTICS v_updated = ROW_COUNT;

            IF v_updated = 0 THEN
                INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                VALUES (v_inventory.product_id, p_warehouse_id, GREATEST(0, v_inventory.adjustment_qty), p_adjustment_date, p_adjustment_type);
            END IF;

            INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
            VALUES (v_inventory.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, v_inventory.current_qty, v_inventory.adjustment_qty, v_inventory.current_qty + v_inventory.adjustment_qty, CURRENT_USER);

            v_total_processed := v_total_processed + 1;
        EXCEPTION
            WHEN OTHERS THEN
                v_error_count := v_error_count + 1;
                INSERT INTO error_log (error_date, error_message, product_id)
                VALUES (NOW(), 'Inventory update failed: ' || SQLERRM, v_inventory.product_id);
        END;
    END LOOP;

    UPDATE inventory_staging
    SET status = 'PROCESSED',
        processed_date = NOW()
    WHERE warehouse_id = p_warehouse_id
        AND status = 'PENDING';

    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_GENERATE_SALES_REPORT(p_year INTEGER, p_quarter INTEGER, p_sales_rep_id INTEGER DEFAULT NULL) RETURNS VOID AS $$
DECLARE
    v_report_id INTEGER;
    v_total_sales NUMERIC := 0;
    v_total_commission NUMERIC := 0;
    v_start_date DATE;
    v_end_date DATE;
    v_rec RECORD;
    v_total_reps INTEGER := 0;
BEGIN
    v_start_date := TO_DATE(p_year::TEXT || '-' || LPAD(((p_quarter - 1) * 3 + 1)::TEXT, 2, '0') || '-01', 'YYYY-MM-DD');
    v_end_date := (DATE_TRUNC('MONTH', v_start_date + INTERVAL '2 months') + INTERVAL '1 month' - INTERVAL '1 day')::DATE;

    v_report_id := nextval('sales_report_seq');

    SELECT COUNT(*) INTO v_total_reps
    FROM sales_representatives sr
    WHERE (p_sales_rep_id IS NULL OR sr.sales_rep_id = p_sales_rep_id);

    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
    VALUES (v_report_id, p_year, p_quarter, CURRENT_TIMESTAMP, v_total_reps);

    FOR v_rec IN
        SELECT sr.sales_rep_id,
               sr.sales_rep_name,
               COALESCE(SUM(s.sale_amount), 0) as total_sales,
               sr.commission_rate,
               COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate as commission_amount
        FROM sales_representatives sr
        LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id
            AND s.sale_date BETWEEN v_start_date AND v_end_date
        WHERE (p_sales_rep_id IS NULL OR sr.sales_rep_id = p_sales_rep_id)
        GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
        ORDER BY total_sales DESC
    LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
        VALUES (v_report_id, v_rec.sales_rep_id, v_rec.sales_rep_name, v_rec.total_sales, v_rec.commission_rate, v_rec.commission_amount);

        v_total_sales := v_total_sales + v_rec.total_sales;
        v_total_commission := v_total_commission + v_rec.commission_amount;
    END LOOP;

    UPDATE sales_report_header
    SET total_sales = v_total_sales,
        total_commission = v_total_commission
    WHERE report_id = v_report_id;

    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;

EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(
