CREATE OR REPLACE PROCEDURE RT_Tang_YW_MainC_InitAwb60Finish()
AS
DECLARE
    Period60 INT;
    Period180 INT;
    Yw_Op_Date TIMESTAMP;
    Yw_Start_Time TIMESTAMP;
    tran_error INT;
    AwbYwDate60 TIMESTAMP;
    AwbYwDate180 TIMESTAMP;
BEGIN
    Period60 := -60;
    Period180 := -180;
    Yw_Start_Time := CURRENT_TIMESTAMP;
    tran_error := 0;
    SELECT MAX(Yw_Op_Date) INTO Yw_Op_Date FROM Tbl_Yw_Log WHERE OPTRESULT = 'RUN';
    IF Yw_Op_Date IS NULL THEN
        Yw_Op_Date := CURRENT_TIMESTAMP;
    END IF;
    AwbYwDate60 := Yw_Op_Date + Period60 * INTERVAL '1 day';
    AwbYwDate180 := Yw_Op_Date + Period180 * INTERVAL '1 day';
    tran_error := 0;
    BEGIN
        DELETE FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN ( SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix AS TmpFinishFlag FROM Tbl_Cargo WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN ( SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix FROM Tbl_Awb_Basic WHERE ( Op_Date < TO_CHAR(AwbYwDate60, 'YYYY-MM-DD') AND Op_Date > TO_CHAR(AwbYwDate180, 'YYYY-MM-DD') ) ) AND FinishFlag != 0 AND Flight_Dep = Flight_Dest GROUP BY AwbPrefix, AwbNo, AwbPostfix );
        INSERT INTO Cargo2YWTemp.Tbl_Awb_Basic_Temp ( AwbPrefix, AwbNo, AwbPostfix, YwTreate ) SELECT DISTINCT AwbPrefix, AwbNo, AwbPostfix, 1 FROM ( SELECT AwbPrefix, AwbNo, AwbPostfix, MIN(FinishFlag) AS TmpFinishFlag FROM Tbl_Cargo WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN ( SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix FROM Tbl_Awb_Basic WHERE ( Op_Date < TO_CHAR(AwbYwDate60, 'YYYY-MM-DD') AND Op_Date > TO_CHAR(AwbYwDate180, 'YYYY-MM-DD') ) ) AND FinishFlag != 0 AND Flight_Dep = Flight_Dest GROUP BY AwbPrefix, AwbNo, AwbPostfix ) Tbl_Temp WHERE TmpFinishFlag > 0;
    EXCEPTION
        WHEN OTHERS THEN
            tran_error := tran_error + 1;
            INSERT INTO TBL_SP_ErrLog ( ErrTable, ErrDate ) VALUES ( 'Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP );
            RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;
    tran_error := 0;
    BEGIN
        UPDATE Cargo2YWTemp.Tbl_Awb_Basic_Temp t SET AwbDate = a.Op_Date, AwbDest = SUBSTRING( a.routing, LENGTH(a.routing) - 2, 3 ), Piece = a.Piece FROM Tbl_Awb_Basic a WHERE a.AwbNo = t.AwbNo AND a.AwbPrefix = t.AwbPrefix AND a.AwbPostfix = t.AwbPostfix;
    EXCEPTION
        WHEN OTHERS THEN
            tran_error := tran_error + 1;
            INSERT INTO TBL_SP_ErrLog ( ErrTable, ErrDate ) VALUES ( 'Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP );
            RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;
    tran_error := 0;
    BEGIN
        UPDATE Cargo2YWTemp.Tbl_Awb_Basic_Temp bt SET YwTreate = 0 FROM ( SELECT SUM(Piece) AS Piece, AwbPrefix, AwbNo, AwbPostfix FROM ( SELECT c.Piece, c.AwbPrefix, c.AwbNo, c.AwbPostfix FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp t JOIN Tbl_Cargo c ON c.Flight_Dep = t.AwbDest AND c.Flight_Dest = t.AwbDest AND c.AwbNo = t.AwbNo AND c.AwbPrefix = t.AwbPrefix AND c.AwbPostfix = t.AwbPostfix ) b GROUP BY AwbPrefix, AwbNo, AwbPostfix ) a WHERE a.AwbNo = bt.AwbNo AND a.AwbPrefix = bt.AwbPrefix AND a.AwbPostfix = bt.AwbPostfix AND bt.Piece = a.Piece;
    EXCEPTION
        WHEN OTHERS THEN
            tran_error := tran_error + 1;
            INSERT INTO TBL_SP_ErrLog ( ErrTable, ErrDate ) VALUES ( 'Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP );
            RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;
    tran_error := 0;
    BEGIN
        DELETE FROM Cargo2YWTemp.Tbl_Cargo_ChargeCorrection_Temp WHERE CCANo IN ( SELECT DISTINCT AwbNo FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp );
        INSERT INTO Cargo2YWTemp.Tbl_Cargo_ChargeCorrection_Temp ( CCAPrefix, CCANo, CCAPostfix ) SELECT DISTINCT CCAPrefix, CCANo, CCAPostfix FROM Tbl_Cargo_ChargeCorrectionDetail a JOIN Cargo2YWTemp.Tbl_Awb_Basic_Temp t ON a.AwbPrefix = t.AwbPrefix AND a.AwbNo = t.AwbNo AND a.AwbPostfix = t.AwbPostfix;
    EXCEPTION
        WHEN OTHERS THEN
            tran_error := tran_error + 1;
            INSERT INTO TBL_SP_ErrLog ( ErrTable, ErrDate ) VALUES ( 'Tbl_Cargo_ChargeCorrection_Temp', CURRENT_TIMESTAMP );
            RAISE EXCEPTION 'Tbl_Cargo_ChargeCorrection_Temp 出现异常';
    END;
END;
/

