CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(
    p_OldCategoryID IN INT,
    p_NewCategoryID IN INT,
    p_EffectiveDate IN TIMESTAMP DEFAULT NULL
)
AS
DECLARE
    v_ProductCount INT;
    v_UpdatedCount INT := 0;
    v_ProductID INT;
    v_ProductName VARCHAR(100);
    v_EffectiveDate TIMESTAMP;
    product_cursor CURSOR FOR
        SELECT ProductID, ProductName FROM Products WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';
BEGIN
    v_EffectiveDate := COALESCE(p_EffectiveDate, CURRENT_TIMESTAMP);

    IF p_OldCategoryID = p_NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO v_ProductCount FROM Products WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';

    IF v_ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', p_OldCategoryID;
        RETURN;
    END IF;

    OPEN product_cursor;
    LOOP
        FETCH product_cursor INTO v_ProductID, v_ProductName;
        EXIT WHEN NOT FOUND;

        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (v_ProductID, p_OldCategoryID, p_NewCategoryID, v_EffectiveDate, CURRENT_USER);

            UPDATE Products SET CategoryID = p_NewCategoryID, LastModifiedDate = v_EffectiveDate, LastModifiedBy = CURRENT_USER
            WHERE ProductID = v_ProductID;

            v_UpdatedCount := v_UpdatedCount + 1;
        EXCEPTION
            WHEN OTHERS THEN
                INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                VALUES (v_ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;

    CLOSE product_cursor;

    UPDATE ProductCategories SET LastUpdateDate = v_EffectiveDate WHERE CategoryID IN (p_OldCategoryID, p_NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', v_UpdatedCount, v_ProductCount;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
/

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(
    p_OldCategoryID INT,
    p_NewCategoryID INT,
    p_EffectiveDate TIMESTAMP DEFAULT NULL
)
AS
DECLARE
    v_EffectiveDate TIMESTAMP;
    v_ProductCount INT;
    v_UpdatedCount INT := 0;
    v_ProductID INT;
    v_ProductName VARCHAR(100);
    v_cursor_open BOOLEAN := FALSE;
    product_cursor CURSOR FOR 
        SELECT ProductID, ProductName 
        FROM Products 
        WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';
BEGIN
    v_EffectiveDate := COALESCE(p_EffectiveDate, CURRENT_TIMESTAMP);

    BEGIN
        IF p_OldCategoryID = p_NewCategoryID THEN
            RAISE EXCEPTION 'Old and new category IDs cannot be the same';
        END IF;

        SELECT COUNT(*) INTO v_ProductCount 
        FROM Products 
        WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';

        IF v_ProductCount = 0 THEN
            RAISE NOTICE 'No active products found in category %', p_OldCategoryID;
            RETURN;
        END IF;

        OPEN product_cursor;
        v_cursor_open := TRUE;

        LOOP
            FETCH product_cursor INTO v_ProductID, v_ProductName;
            EXIT WHEN NOT FOUND;

            BEGIN
                INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
                VALUES (v_ProductID, p_OldCategoryID, p_NewCategoryID, v_EffectiveDate, CURRENT_USER);

                UPDATE Products 
                SET CategoryID = p_NewCategoryID, 
                    LastModifiedDate = v_EffectiveDate, 
                    LastModifiedBy = CURRENT_USER
                WHERE ProductID = v_ProductID;

                v_UpdatedCount := v_UpdatedCount + 1;
            EXCEPTION WHEN OTHERS THEN
                INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                VALUES (v_ProductID, SQLERRM, CURRENT_TIMESTAMP);
            END;
        END LOOP;

        CLOSE product_cursor;
        v_cursor_open := FALSE;

        UPDATE ProductCategories 
        SET LastUpdateDate = v_EffectiveDate 
        WHERE CategoryID IN (p_OldCategoryID, p_NewCategoryID);

        RAISE NOTICE 'Category update completed. % products updated out of % total.', v_UpdatedCount, v_ProductCount;

    EXCEPTION WHEN OTHERS THEN
        IF v_cursor_open THEN
            CLOSE product_cursor;
        END IF;
        RAISE EXCEPTION 'Category update failed: %', SQLERRM;
    END;
END;
/

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(
    p_OldCategoryID IN INT,
    p_NewCategoryID IN INT,
    p_EffectiveDate IN TIMESTAMP DEFAULT NULL
)
AS
DECLARE
    v_ProductCount INT;
    v_UpdatedCount INT := 0;
    v_ProductID INT;
    v_ProductName VARCHAR(100);
    v_EffectiveDate TIMESTAMP;
    product_cursor CURSOR FOR
        SELECT ProductID, ProductName 
        FROM Products 
        WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';
BEGIN
    v_EffectiveDate := COALESCE(p_EffectiveDate, CURRENT_TIMESTAMP);

    IF p_OldCategoryID = p_NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO v_ProductCount 
    FROM Products 
    WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';

    IF v_ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', p_OldCategoryID;
        RETURN;
    END IF;

    OPEN product_cursor;
    LOOP
        FETCH product_cursor INTO v_ProductID, v_ProductName;
        EXIT WHEN NOT FOUND;

        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (v_ProductID, p_OldCategoryID, p_NewCategoryID, v_EffectiveDate, CURRENT_USER);

            UPDATE Products 
            SET CategoryID = p_NewCategoryID,
                LastModifiedDate = v_EffectiveDate,
                LastModifiedBy = CURRENT_USER
            WHERE ProductID = v_ProductID;

            v_UpdatedCount := v_UpdatedCount + 1;
        EXCEPTION WHEN OTHERS THEN
            INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
            VALUES (v_ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;

    CLOSE product_cursor;

    UPDATE ProductCategories 
    SET LastUpdateDate = v_EffectiveDate
    WHERE CategoryID IN (p_OldCategoryID, p_NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', v_UpdatedCount, v_ProductCount;

EXCEPTION WHEN OTHERS THEN
    RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
/

CREATE OR REPLACE PROCEDURE GETEMPLOYEESTATS(p_dept_id IN NUMBER, p_result OUT CLOB) IS
  TYPE stats_rec IS RECORD (total_emp NUMBER, avg_salary NUMBER, max_salary NUMBER, active_projects NUMBER);
  v_stats stats_rec;
  v_employee_id NUMBER;
  v_last_name VARCHAR2(50);
  v_salary NUMBER(8,2);
  v_cursor SYS_REFCURSOR;
  v_sql VARCHAR2(1000);
  v_dept_name VARCHAR2(50);
BEGIN
  SELECT department_name INTO v_dept_name FROM DEPARTMENTS WHERE department_id = p_dept_id;
  SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_stats.total_emp, v_stats.avg_salary, v_stats.max_salary FROM EMPLOYEES WHERE department_id = p_dept_id;
  SELECT COUNT(*) INTO v_stats.active_projects FROM PROJECTS WHERE department_id = p_dept_id AND status = 'ACTIVE';
  v_sql := 'SELECT employee_id, last_name, salary FROM (SELECT employee_id, last_name, salary FROM employees WHERE department_id = :1 ORDER BY salary DESC) WHERE ROWNUM <= 5';
  OPEN v_cursor FOR v_sql USING p_dept_id;
  p_result := '{"department": "' || v_dept_name || '", "total_employees": ' || v_stats.total_emp || ', "salary_stats": {"average": ' || v_stats.avg_salary || ', "max": ' || v_stats.max_salary || '}, "active_projects": ' || v_stats.active_projects || ', "top_earners": [';
  LOOP
    FETCH v_cursor INTO v_employee_id, v_last_name, v_salary;
    EXIT WHEN v_cursor%NOTFOUND;
    p_result := p_result || '{"id": ' || v_employee_id || ', "name": "' || v_last_name || '", "salary": ' || v_salary || '},';
  END LOOP;
  p_result := RTRIM(p_result, ',') || ']}';
  CLOSE v_cursor;
EXCEPTION
  WHEN NO_DATA_FOUND THEN
    p_result := '{"error": "Department not found"}';
  WHEN OTHERS THEN
    p_result := '{"error": "' || SQLERRM || '"}';
END;
/

CREATE OR REPLACE PROCEDURE p_job_create_bond_info_all()
AS
DECLARE
    v_colname VARCHAR(100);
    v_sql VARCHAR(2000);
    c_names CURSOR FOR 
        SELECT column_name 
        FROM information_schema.columns 
        WHERE LOWER(table_name) = 't_pj_bond_info_all' 
        AND LOWER(column_name) LIKE 'def%';
BEGIN
    TRUNCATE TABLE tmp_bond_info_all_20010;

    INSERT INTO tmp_bond_info_all_20010(f_val, def_id, id)
    SELECT field_value, FIELD_DEFINITION_ID, t2.ID 
    FROM T_OBJECT_FIELD_DATA t1, T_PJ_Bond_Info t2 
    WHERE t1.OBJECT_NO = 20010 
    AND t1.FIELD_VALUE IS NOT NULL 
    AND t1.parent_id = t2.APPLY_ID;

    TRUNCATE TABLE tmp_bond_info_all_20020;

    INSERT INTO tmp_bond_info_all_20020(f_val, def_id, id)
    SELECT field_value, FIELD_DEFINITION_ID, t2.id AS bondId 
    FROM T_OBJECT_FIELD_DATA t1, T_PJ_Bond_Info t2 
    WHERE t1.OBJECT_NO = 20020 
    AND t1.FIELD_VALUE IS NOT NULL 
    AND t1.PARENT_ID = t2.TEMP_BONDID;

    TRUNCATE TABLE t_pj_bond_info_all;

    INSERT INTO t_pj_bond_info_all(id, temp_bondid, apply_id)
    SELECT t1.ID, t1.TEMP_BONDID, t1.APPLY_ID 
    FROM T_PJ_Bond_Info t1 
    WHERE 1 = 1;

    OPEN c_names;
    LOOP
        FETCH c_names INTO v_colname;
        EXIT WHEN NOT FOUND;

        v_sql := 'UPDATE t_pj_bond_info_all a SET ' || v_colname || ' = b.f_val FROM tmp_bond_info_all_20020 b WHERE a.id = b.id AND b.def_id = ' || REPLACE(v_colname, 'def_', '');
        EXECUTE IMMEDIATE v_sql;

        v_sql := 'UPDATE t_pj_bond_info_all a SET ' || v_colname || ' = b.f_val FROM tmp_bond_info_all_20010 b WHERE a.id = b.id AND a.' || v_colname || ' IS NULL AND b.def_id = ' || REPLACE(v_colname, 'def_', '');
        EXECUTE IMMEDIATE v_sql;
    END LOOP;
    CLOSE c_names;
END;
/

