$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION archive_old_records(p_archive_date DATE) RETURNS void AS $$
DECLARE
    v_deleted_rows INTEGER;
BEGIN
    INSERT INTO archive_table (record_id, record_data)
    SELECT record_id, record_data FROM active_data WHERE record_date < p_archive_date;
    DELETE FROM active_data WHERE record_date < p_archive_date;
    GET DIAGNOSTICS v_deleted_rows = ROW_COUNT;
    INSERT INTO audit_log (log_message) VALUES (v_deleted_rows || ' rows archived.');
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION GETEMPLOYEESTATS(p_dept_id INTEGER, OUT p_result TEXT) AS $$
DECLARE
    v_total_emp INTEGER;
    v_avg_salary NUMERIC;
    v_max_salary NUMERIC;
    v_active_projects INTEGER;
    v_dept_name VARCHAR(50);
    v_employee_id INTEGER;
    v_last_name VARCHAR(50);
    v_salary NUMERIC(8,2);
    v_cursor refcursor;
    v_sql TEXT;
BEGIN
    SELECT department_name INTO STRICT v_dept_name FROM DEPARTMENTS WHERE department_id = p_dept_id;
    SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_total_emp, v_avg_salary, v_max_salary FROM EMPLOYEES WHERE department_id = p_dept_id;
    SELECT COUNT(*) INTO v_active_projects FROM PROJECTS WHERE department_id = p_dept_id AND status = 'ACTIVE';
    v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';
    OPEN v_cursor FOR EXECUTE v_sql USING p_dept_id;
    p_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || v_total_emp || ',  "salary_stats": {    "average": ' || v_avg_salary || ',    "max": ' || v_max_salary || '  },  "active_projects": ' || v_active_projects || ',  "top_earners": [';
    LOOP
        FETCH v_cursor INTO v_employee_id, v_last_name, v_salary;
        EXIT WHEN NOT FOUND;
        p_result := p_result || '    {"id": ' || v_employee_id || ', "name": "' || v_last_name || '", "salary": ' || v_salary || '},';
    END LOOP;
    p_result := RTRIM(p_result, ',') || '  ]}';
    CLOSE v_cursor;
EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
    WHEN OTHERS THEN
        p_result := '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE usp_UpdateOrderStatus(
    p_CustomerID IN INTEGER,
    p_OldStatus IN VARCHAR(20),
    p_NewStatus IN VARCHAR(20),
    p_RowsUpdated OUT INTEGER,
    p_ErrorMessage OUT VARCHAR(200)
)
AS
DECLARE
    v_ProcessingFee DECIMAL(10,2) := 0;
    fee_cursor CURSOR FOR 
        SELECT Fee FROM OrderDetails 
        WHERE OrderID IN (SELECT OrderID FROM Orders WHERE CustomerID = p_CustomerID AND Status = p_NewStatus);
BEGIN
    IF p_OldStatus = p_NewStatus THEN
        p_ErrorMessage := '新旧状态不能相同';
        RETURN;
    END IF;

    UPDATE Orders 
    SET Status = p_NewStatus, LastUpdated = CURRENT_TIMESTAMP
    WHERE CustomerID = p_CustomerID 
    AND Status = p_OldStatus 
    AND OrderDate > CURRENT_TIMESTAMP - INTERVAL '3 months';

    GET DIAGNOSTICS p_RowsUpdated = ROW_COUNT;

    IF p_RowsUpdated = 0 THEN
        p_ErrorMessage := '未找到符合条件的订单';
        RETURN;
    END IF;

    INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
    SELECT OrderID, p_OldStatus, p_NewStatus, CURRENT_TIMESTAMP
    FROM Orders
    WHERE CustomerID = p_CustomerID AND Status = p_NewStatus;

    OPEN fee_cursor;
    LOOP
        FETCH fee_cursor INTO v_ProcessingFee;
        EXIT WHEN fee_cursor%NOTFOUND;
        v_ProcessingFee := v_ProcessingFee * 0.95;
    END LOOP;
    CLOSE fee_cursor;

    p_ErrorMessage := '更新成功';

EXCEPTION
    WHEN OTHERS THEN
        p_ErrorMessage := SQLERRM;
END;
/

