DROP PROCEDURE IF EXISTS SP_KSH_PERSON_FEE;
CREATE PROCEDURE SP_KSH_PERSON_FEE (IN V_USER_ID VARCHAR(255))
BEGIN
DECLARE V_TEMP_COMPANY VARCHAR(20);
SELECT HP.COMPANY_CODE INTO V_TEMP_COMPANY FROM KSH_USER_HOME_PAGE HP WHERE HP.USER_ID =V_USER_ID;
IF V_TEMP_COMPANY<>'00000000' THEN
SELECT UUID() AS UUID, TEMP.INVOICE_SORT_NAME, SUM(TEMP.TARGET_FEE) AS TARGET_FEE, SUM(TEMP.BASE_AMOUNT) AS BASE_AMOUNT, (CASE WHEN SUM(TEMP.TARGET_FEE)= 0 THEN 0 ELSE ROUND(SUM(TEMP.BASE_AMOUNT)/SUM(TEMP.TARGET_FEE),2) END) AS RATE, SUM(TEMP.OVERDUE_CNT) AS OVERDUE_CNT FROM ( SELECT FD.INVOICE_SORT_NAME,FD.COMPANY_SORT_SEQ, VC.VESSEL_CODE, SUM(VC.TARGET_FEE) AS TARGET_FEE, SUM(VC.BASE_AMOUNT) AS BASE_AMOUNT, (CASE WHEN SUM(TARGET_FEE)<SUM(BASE_AMOUNT) THEN 1 ELSE 0 END) AS OVERDUE_CNT FROM KSH_CSCE_VESSEL_FEE VC INNER JOIN KSH_FEE_INVOICE_DEFINE FD ON IFNULL(VC.GROUP_SORT_DETAIL, VC.GROUP_SORT) = FD.INVOICE_SORT_DETAIL AND FD.COMPANY_CODE= VC.COMPANY_CODE WHERE VC.TARGET_YEAR = DATE_FORMAT(SYSDATE(), '%Y') AND EXISTS (select 1 from ksh_user_manage_ship um where um.user_id = V_USER_ID and um.vessel_code = VC.vessel_code) GROUP BY FD.INVOICE_SORT_NAME,FD.COMPANY_SORT_SEQ,VC.VESSEL_CODE) TEMP GROUP BY TEMP.INVOICE_SORT_NAME;
ELSE
SELECT UUID() AS UUID, TEMP.INVOICE_SORT_NAME, SUM(TEMP.TARGET_FEE) AS TARGET_FEE, SUM(TEMP.BASE_AMOUNT) AS BASE_AMOUNT, (CASE WHEN SUM(TEMP.TARGET_FEE)= 0 THEN 0 ELSE ROUND(SUM(TEMP.BASE_AMOUNT)/SUM(TEMP.TARGET_FEE),2) END) AS RATE, SUM(TEMP.OVERDUE_CNT) AS OVERDUE_CNT FROM ( SELECT FD.GROUP_SORT_NAME AS INVOICE_SORT_NAME,FD.Group_Sort_Seq, VC.VESSEL_CODE, SUM(VC.TARGET_FEE) AS TARGET_FEE, SUM(VC.BASE_AMOUNT) AS BASE_AMOUNT, (CASE WHEN SUM(TARGET_FEE)<SUM(BASE_AMOUNT) THEN 1 ELSE 0 END) AS OVERDUE_CNT FROM KSH_CSCE_VESSEL_FEE VC INNER JOIN KSH_FEE_INVOICE_DEFINE FD ON IFNULL(VC.GROUP_SORT_DETAIL, VC.GROUP_SORT) = FD.INVOICE_SORT_DETAIL AND FD.GROUP_SORT IS NOT NULL WHERE VC.TARGET_YEAR = DATE_FORMAT(SYSDATE(), '%Y') AND EXISTS (select 1 from ksh_user_manage_ship um where um.user_id = V_USER_ID and um.vessel_code = VC.vessel_code) GROUP BY FD.GROUP_SORT_NAME,FD.Group_Sort_Seq,VC.VESSEL_CODE) TEMP GROUP BY TEMP.INVOICE_SORT_NAME, TEMP.Group_Sort_Seq ORDER BY TEMP.Group_Sort_Seq;
END IF;
END;

CREATE OR REPLACE PROCEDURE SP_FEE_CSBC_HOUR_JOB() AS $$
