        DELETE FROM FMM_CSBC_MAINTENANCE_TZ_DETAIL MD WHERE MD.UPDATE_TIME>=date_trunc('day',NOW());
        INSERT INTO FMM_CSBC_MAINTENANCE_TZ_DETAIL select * from VW_CSBC_MAINTENANCE_TZ_DETAIL WHERE UPDATE_TIME>=date_trunc('day',NOW());
        PERFORM P_LOG_EXCEPTION(SQLERRM || 'insert 结束时间:' || NOW(), 'SP_FEE_CSBC_HOUR_JOB');
