CREATE OR REPLACE PROCEDURE SP_FHD_ALERT_IMT (I_VESSEL_CODE IN VARCHAR, I_MANAGER IN VARCHAR, OUT OUT_CURSOR REFCURSOR ) AS $$
DECLARE
  OBJ_TAB_TYPE OBJECT_TABLE := OBJECT_TABLE();
  I INTEGER := 1;
  M RECORD;
BEGIN
  FOR M IN (SELECT VESSEL_CODE, VESSEL_NAME, SAFETY_COMPANY, FN_GET_VESSEL_MNT_CODE(FVIA.VESSEL_CODE, FVIA.POSI_DATE, '5', '0') as SAFETY_COMPANY_CODE, MMSI, POSI_DATE, POSI_LAT, POSI_LONG, AREA_NAME FROM FHD_VESSEL_IN_AREA FVIA WHERE FVIA.SORTCODE=(SELECT MAX(SORTCODE) AS SORTCODE FROM FHD_VESSEL_IN_AREA) AND FVIA.CREATED_DTM_LOC > NOW()- interval '1 hour' /24 AND (I_VESSEL_CODE IS NULL OR FVIA.VESSEL_CODE=I_VESSEL_CODE) AND (I_MANAGER IS NULL OR FN_GET_VESSEL_MNT_CODE(FVIA.VESSEL_CODE,FVIA.CREATED_DTM_LOC,'5', '0')=I_MANAGER) )
  LOOP
    OBJ_TAB_TYPE.EXTEND;
    OBJ_TAB_TYPE(I) := OBJECTTABTYPE(M.VESSEL_CODE, M.VESSEL_NAME, NULL, NULL, NULL, NULL, M.POSI_LONG, M.POSI_LAT, M.POSI_DATE, M.AREA_NAME, M.SAFETY_COMPANY, gen_random_uuid(), M.SAFETY_COMPANY_CODE, M.MMSI);
    I := I + 1;
  END LOOP;

  UPDATE imt_monitor_main IMM SET STATUS=1, MONITOR_ENDDATE=null WHERE ROUND(EXTRACT(EPOCH FROM (NOW() - IMM.MONITOR_ENDDATE)) / 3600,2)<47 AND IMM.MONITOR_ENDDATE IS NOT NULL AND IMM.MONITOR_CONF_ID='FHD_ALERT' AND IMM.VESSEL_CODE IN ( SELECT VESSEL_CODE FROM UNNEST(OBJ_TAB_TYPE) AA WHERE fn_fhd_alert_is_send(AA.VESSEL_CODE)=0);

  insert into imt_monitor_detail(monitor_detail_id,monitor_main_id,vessel_code,feedback_mode,feedback_user,feedback_explain,feedback_date,MONITOR_DETAIL_MODE, COMPANY_CODE,Created_By_User,CREATED_OFFICE,CREATED_DTM_LOC,RECORD_VERSION,PRINCIPAL_GROUP_CODE)
  select gen_random_uuid(),AA.ETA_PORT_NAME,AA.VESSEL_CODE,'自动记录','系统','船舶驶入防海盗区，监控启动',NOW(),'1.1', '00000000','YLZ','00000000',NOW(),0,0 FROM UNNEST(OBJ_TAB_TYPE) AA WHERE AA.VESSEL_CODE IN ( SELECT IMM.VESSEL_CODE FROM imt_monitor_main IMM WHERE MONITOR_CONF_ID='FHD_ALERT' AND MONITOR_STARTDATE IS NOT NULL AND MONITOR_ENDDATE IS NULL);

  INSERT INTO imt_monitor_main(MONITOR_MAIN_ID,MONITOR_CONF_ID,VESSEL_CODE,Manage_Company_Code,STATUS, COMPANY_CODE,Created_By_User,CREATED_OFFICE,CREATED_DTM_LOC,RECORD_VERSION,PRINCIPAL_GROUP_CODE, MONITOR_STARTDATE)
  SELECT AA.ETA_PORT_NAME,'FHD_ALERT' AS MONITOR_CONF_ID,AA.VESSEL_CODE,ETA_TIME AS MANAGER_NAME,1, '00000000','YLZ','00000000',NOW(),0,0,AA.ACTIVITY_DATE FROM UNNEST(OBJ_TAB_TYPE) AA WHERE AA.VESSEL_CODE IN ( SELECT IMM.VESSEL_CODE FROM imt_monitor_main IMM WHERE MONITOR_CONF_ID='FHD_ALERT' AND MONITOR_STARTDATE IS NOT NULL AND MONITOR_ENDDATE IS NULL);

  OPEN OUT_CURSOR FOR SELECT VESSEL_CODE, VESSEL_NAME, AA.MANAGER_NAME AS SAFETY_COMPANY, AA.MMSI AS MMSI, AA.ACTIVITY_DATE AS POSI_DATE, AA.LATITUDE AS POSI_LAT, AA.LONGTITUDE AS POSI_LONG, AREA_NAME FROM UNNEST(OBJ_TAB_TYPE) AA WHERE fn_fhd_alert_is_send(AA.VESSEL_CODE)=1;

EXCEPTION
  WHEN OTHERS THEN
    RAISE NOTICE '错误: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;
